mapper.select:
 select * from
(select rownum rn, tt.* from
(select * from board order by ref desc, step asc)tt 
)where rn>=#{startNum} and rn<=#{endNum}


select * from
(select rownum rn, tt.* from
(select * from board
 where ${searchOption} like '%'||#{keyword}||'%'
 order by ref desc, step asc)tt 
)where rn>=#{startNum} and rn<=#{endNum}

mapper.insert:
 begin
  	update board set step=step+1 where ref=#{ref} and step > #{step};

  	 insert into board values(seq_board.nextVal, #{id}, #{name}, #{email},
	 #{subject},#{content}, #{ref},#{lev}+1,#{step}+1,#{pseq}, 
	 0, 
	 0, 
	 sysdate);

	 update board set reply=reply+1 where seq=#{pseq};
 end;

mapper.delete:
begin
	update board set reply=reply-1 where seq=(select pseq from board where seq=#{seq});
	update board set subject = '[원글이 삭제된 답글]'||subject where pseq=#{seq};
	delete from board where seq=#{seq};
end;
